import { createSlice, configureStore } from "@reduxjs/toolkit";

const visSlice = createSlice({
  name: "vis",
  initialState: {
    cartVis: false,
    testEx: true,
    bookEx: false,
    dvdEx: false,
    cartContent: [{ title: "test ", quantity: 1, total: 18, price: 6, id: 1 }],
  },
  reducers: {
    cartVis(state) {
      state.cartVis = !state.cartVis;
    },

    addToCart(state, action) {
      state.cartContent = [...state.cartContent, action.payload];
    },
    addQuantity(state, action) {
      state.cartContent.forEach((el) => {
        if (el.price === action.payload) {
          el.quantity++;
        }
      });
    },
    subQuantity(state, action) {
      state.cartContent.forEach((el) => {
        if (el.price === action.payload) {
          el.quantity--;
        }
      });
    }
  },
});

const store = configureStore({
  reducer: visSlice.reducer,
});
export const visSliceActions = visSlice.actions;
export default store;
