import Card from "../UI/Card";
import classes from "./ProductItem.module.css";
import { useDispatch, useSelector } from "react-redux";
import { visSliceActions } from "../../store/redux";

const ProductItem = (props) => {
  const cartContent = useSelector((state) => state.cartContent);
  const dispatch = useDispatch();
  const { title, price, description,quantity } = props;
  const clickHandler = () => {
     let adding=true;
    cartContent.forEach((el) => {

      if (el.price === price) {
        adding=false;
        dispatch(visSliceActions.addQuantity(price))
        return;
      }
    });
    if(!adding){
      return;
    }

    dispatch(
      visSliceActions.addToCart({
        title,
        quantity,
        total: price*quantity,
        price,
        id: price,
      })
    );
  };

  return (
    <li className={classes.item}>
      <Card>
        <header>
          <h3>{title}</h3>
          <div className={classes.price}>${price.toFixed(2)}</div>
        </header>
        <p>{description}</p>
        <div className={classes.actions}>
          <button onClick={clickHandler}>Add to Cart</button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
