import { createSlice } from "@reduxjs/toolkit";

const uiSlice = createSlice({
  name: "ui",
  initialState: {
    cartVis: false,
  },
  reducers: {
    cartVis(state) {
      state.cartVis = !state.cartVis;
    },
  },
});

export const uiSliceActions=uiSlice.actions;
export default uiSlice.reducer;
