import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { uiSliceActions } from "./store/uiSlice";
import Notification from "./components/UI/Notifactions";

function App() {
  const cartVis = useSelector((state) => state.ui.cartVis);
  const cartContent = useSelector((state) => state.cart.cartContent);
  const showNotification = useSelector((state) => state.ui.showNotification);
  const dispatch = useDispatch();

  useEffect(() => {
    const sendData = async () => {
      dispatch(
        uiSliceActions.showNotification({
          status: "pending",
          title: "loading...",
          message: "wating for order to excute",
        })
      );
      const responce = await fetch(
        "https://react2-fe651-defaul-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cartContent),
        }
      );
      if (!responce.ok) {
        throw new Error("something went rong");
      }
      dispatch(
        uiSliceActions.showNotification({
          status: "success",
          title: "successed",
          message: "your order is successed",
        })
      );
    };
    sendData().catch((message) => {
      dispatch(
        uiSliceActions.showNotification({
          status: "error",
          title: "Error",
          message,
        })
      );
    });
  }, [cartContent, dispatch]);

  return (
    <Layout>
      {showNotification && (
        <Notification
          status={showNotification.status}
          title={showNotification.title}
          message={showNotification.message}
        ></Notification>
      )}
      {cartVis && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
